import React, { useEffect, useState, setState } from "react";
import ReactDOM from "react-dom";
import img3 from "../src/img/img3.jpg";
import img from "../src/img/img.jpg";
import img4 from "../src/img/img4.jpg";
import img5 from "../src/img/img5.jpg";
import img6 from "../src/img/img6.jpg";
import img7 from "../src/img/img7.jpg";
import img8 from "../src/img/img8.jpg";
import img9 from "../src/img/img9.jpg";
import "./index.css";
import axios from "axios";

export default function App() {
  const [movies] = useState([]);

  // axios
  //   .get("https://www.omdbapi.com/?apikey=6a8e2b9b&son&page=1")
  //   .then((response) => setMovies(response.data.Search));

  this.aramaYap = this.aramaYap.bind();

  function aramaYap() {
    var txtSearch = document.getElementById("txtSearch");
    alert(txtSearch.value);

    var that = this;
    axios
      .get("https://www.omdbapi.com/?apikey=6a8e2b9b&son&page=1")
      .then((response) => that.setState({ movies: response.data.Search }));
  }

  return (
    <div className="container site">
      <nav className="navbar navbar-expand-lg bg-light">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">
            MOVİESAPP
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="#">
                  Ana sayfa
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">
                  Film Türleri
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">
                  Yabancı/Türkçe
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <nav className="navbar bg-light searchbutton">
        <div className="container-fluid">
          <form className="d-flex" role="search">
            <input
              className="form-control me-2"
              type="text"
              placeholder="Search"
              aria-label="Search"
              id="txtSearch"
            />

            <button
              className="btn btn-outline-success"
              type="button"
              onClick={() => {
                aramaYap();
              }}
            >
              Search
            </button>
          </form>
        </div>
      </nav>

      <div className="col-md-12 ">
        {movies.map((movie) => {
          return (
            <div key={movie.Title} className="col-md-4 col-sm-5 filmbox">
              <img src={movie.Poster} className="resim" alt="..." />
              <div className="card-body">
                <h5 className="card-title">{movie.Title}</h5>
                <a href="#" className="btn btn-primary">
                  Filmi izle
                </a>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
ReactDOM.render(<App />, document.getElementById("root"));
